package Selenium;

public class DoWhileLoop {

	public static void main(String[] args) {
		
		// TODO Auto-generated method stub
		int p=1;
		
		do {
			System.out.println(p);
			
		
			if(p%5==0) {
				System.out.println("hi");
			}
				
			p++;	
			}
			
		
		while(p<=20);
		do {
			System.out.println("Ashwini");
		}
		while(false);
		

	}

}
